mapper.select:
select *
from user_tb
where email = #{email} and password = #{password}

mapper.update:
update user_tb
set password = #{password}, tel = #{tel}
where email = #{email}

mapper.select:
SELECT id FROM user_tb 
where email=#{email} and password=#{password} and name=#{name} and gender=#{gender} and 
birth=#{birth} and tel=#{tel}
